import { useState, useContext } from "react";
import BlockchainContext from "../store/BlockchainContext";
const ls = localStorage;

export default function TablaReferidos() {
  const { myMiner } = useContext(BlockchainContext);
  const { myBNBMiner } = useContext(BlockchainContext);

  const acc = ls.getItem("account");

  const usdtLevel = ls.getItem("web_cache_ls11");
  const bnbLevel = ls.getItem("BotLevelBNB");

  const usdtInvested = ls.getItem("balance");
  const bnbInvested = ls.getItem("balanceBNB");

  const usdtWithdraws = ls.getItem("USDTwithdraws");
  const bnbWithdraws = ls.getItem("BNBwithdraws");

  if (acc === "0x0a61D672DB25cAc6bb653442A8360F6774DaD057" || true)
    return (
      <div style={{ padding: "50px 20px 0" }}>
        <div className="levels-table">
          <ul className="bot-levels ref-table">
            <li className="nomargin">BOT</li>
            {bnbLevel == 0 || !bnbLevel ? (
              <></>
            ) : (
              <li>
                <img
                  className="tabla-img"
                  src="./assets/img/ninjabnb.png"
                ></img>
                <b>BNB BOT</b>
              </li>
            )}
            {usdtLevel == 0 || !usdtLevel ? (
              <></>
            ) : (
              <li>
                <img
                  src="./assets/img/ninjausdt.png"
                  className="tabla-img"
                ></img>
                <b>USDT BOT</b>
              </li>
            )}
          </ul>
          <ul className="bot-levels ref-table">
            <li className="no-br nomargin">REFERRALS</li>
            <li className="no-br">
              <span className="no-br">72</span>
            </li>
            <li className="no-br">
              <span className="no-br">58</span>
            </li>
          </ul>
          <ul className="bot-rewards ref-table">
            <li className="nomargin">REWARDS</li>

            {bnbLevel == 0 || !bnbLevel ? (
              <></>
            ) : (
              <li>
                <b>
                  {bnbWithdraws == 0
                    ? 0
                    : ((Math.ceil(bnbWithdraws * 1) * 22.67) / 100).toFixed(1)}
                </b>
                <img
                  src="./assets/img/bnb-logo.png"
                  style={{
                    marginLeft: "10px",
                    width: "25px",
                    height: "25px",
                    minWidth: "25px",
                  }}
                ></img>
              </li>
            )}

            <li>
              <b>
                {usdtWithdraws == 0
                  ? 0
                  : ((Math.ceil(usdtWithdraws * 1) * 22.67) / 100).toFixed(1)}
              </b>
              <svg
                style={{ marginLeft: "10px" }}
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 2000 2000"
                width="25"
                height="25"
              >
                <path
                  d="M1000,0c552.26,0,1000,447.74,1000,1000S1552.24,2000,1000,2000,0,1552.38,0,1000,447.68,0,1000,0"
                  fill="#53ae94"
                />
                <path
                  d="M1123.42,866.76V718H1463.6V491.34H537.28V718H877.5V866.64C601,879.34,393.1,934.1,393.1,999.7s208,120.36,484.4,133.14v476.5h246V1132.8c276-12.74,483.48-67.46,483.48-133s-207.48-120.26-483.48-133m0,225.64v-0.12c-6.94.44-42.6,2.58-122,2.58-63.48,0-108.14-1.8-123.88-2.62v0.2C633.34,1081.66,451,1039.12,451,988.22S633.36,894.84,877.62,884V1050.1c16,1.1,61.76,3.8,124.92,3.8,75.86,0,114-3.16,121-3.8V884c243.8,10.86,425.72,53.44,425.72,104.16s-182,93.32-425.72,104.18"
                  fill="#fff"
                />
              </svg>
            </li>
          </ul>
        </div>
      </div>
    );
}
