import { useState, useContext } from "react";
import BlockchainContext from "../store/BlockchainContext";
import whiteSpinner from "../assets/img/spin.svg";
import { toast } from "react-toastify";

const DonateSection = () => {
  const [amount, setAmount] = useState();
  const { handleDonate, donatepending, donateText, approveBusd, account } =
    useContext(BlockchainContext);

  const donate = () => {
    if (new Date().getTime() > 1652554800000) {
      if (donateText === "Enable") {
        approveBusd(account);
      } else {
        if (Number(amount) > 0) {
          handleDonate(amount);
        } else {
          toast.error("Enter valid amount", {
            position: "bottom-right",
          });
        }
      }
    } else {
      toast.warn("Launch date is Saturday 14th May 2022 at 7pm UTC", {
        position: "bottom-right",
      });
    }
  };

  return <div></div>;
};

export default DonateSection;
